import React, { useState } from 'react';
import {
	HERONAME,
	ALTEREGO,
	FIRSTAPPEARANCE,
	PUBLISHER,
} from '../helpers/placeholders';

const Search = (props) => {
	const [heroName, setHeroName] = useState(undefined);
	const [alterEgo, setAlterEgo] = useState(undefined);
	const [firstAppearance, setFirstAppearance] = useState(undefined);
	const [publisher, setPublisher] = useState(undefined);

	const handleFormSubmit = (e) => {
		e.preventDefault();
		const queryObject = {
			heroName,
			alterEgo,
			firstAppearance,
			publisher,
		};

		props.onSubmit(queryObject);
	};

	const handleChange = (placeholder, text) => {
		console.log(placeholder === HERONAME);
		if (placeholder === HERONAME) {
			console.log(`Name`);
			setHeroName(text);
		}
		// case :

		// 	break;
		// case placeholder === ALTEREGO:
		// 	setAlterEgo(text);
		// 	break;
		// case placeholder === FIRSTAPPEARANCE:
		// 	setFirstAppearance(text);
		// 	break;
		// case placeholder === PUBLISHER:
		// 	setPublisher(text);
		// 	break;
		// default:
		// 	return;
	};

	return (
		<form onSubmit={(e) => handleFormSubmit(e)}>
			<input
				type="text"
				value={heroName}
				placeholder="Hero Name"
				onChange={(e) => handleChange(HERONAME, e.target.value)}
			/>
			<input
				type="text"
				value={alterEgo}
				placeholder="Alter Ego"
				onChange={(e) => handleChange(ALTEREGO, e.target.value)}
			/>
			<input
				type="text"
				value={firstAppearance}
				placeholder="First Appearance"
				onChange={(e) => handleChange(FIRSTAPPEARANCE, e.target.value)}
			/>
			<p>
				<label>Publisher</label>
				<select id="publisher-list">
					<option value="Marvel Comics">Marvel</option>
					<option value="DC Comics">DC</option>
				</select>
			</p>
			<button>Submit</button>
		</form>
	);
};

export default Search;
