import React, { useEffect, useState } from 'react';
import axios from 'axios';

import Search from './Search';
import HeroesList from './HeroesList';
import { url } from '../api/superheroesApi';

const App = () => {
	const [superheroes, setSuperHeroes] = useState(null);
	const [query, setQuery] = useState({
		alterEgo: '',
		firstAppearance: '',
		heroName: '',
		publisher: '',
	});
	const [loading, setLoading] = useState(true);

	const fetchHeroQuery = async () => {
		let queryString = '';
		if (query.firstAppearance !== '') {
			queryString = queryString.concat(
				`first_appearance=${query.firstAppearance}&`
			);
		}
		if (query.publisher !== '') {
			queryString = queryString.concat(`publisher=${query.publisher}&`);
		}
		if (query.alterEgo !== '') {
			queryString = queryString.concat(`alter_ego=${query.alterEgo}&`);
		}
		if (query.heroName !== '') {
			queryString = queryString.concat(`superhero=${query.heroName}&`);
		}

		const { data } = await url.get(queryString);
		setSuperHeroes(data);
		setLoading(false);
	};

	useEffect(() => {
		setLoading(true);
		fetchHeroQuery();
	}, [query]);

	if (loading) {
		return <div>Wrangling Heroes!</div>;
	} else {
		return (
			<div>
				<div>
					<Search onSubmit={(object) => setQuery(object)} />
				</div>
				<HeroesList heroState={superheroes} />
			</div>
		);
	}
};

export default App;
