import React, { useState } from 'react';
import {
	HERONAME,
	ALTEREGO,
	FIRSTAPPEARANCE,
	PUBLISHER,
} from '../helpers/placeholders';

const Search = (props) => {
	const [heroName, setHeroName] = useState(null);
	const [alterEgo, setAlterEgo] = useState(null);
	const [firstAppearance, setFirstAppearance] = useState(null);
	const [publisher, setPublisher] = useState(null);

	const handleFormSubmit = (placeholder, text) => {};

	const handleChange = (placeholder, text) => {
		switch (placeholder) {
			case placeholder === HERONAME:
				setHeroName(text);
				break;
			case placeholder === ALTEREGO:
				setAlterEgo(text);
				break;
			case placeholder === FIRSTAPPEARANCE:
				setFirstAppearance(text);
				break;
			case placeholder === PUBLISHER:
				setPublisher(text);
				break;
			default:
				return;
		}
	};

	return (
		<form>
			<input
				type="text"
				value={heroName}
				placeholder={HERONAME}
				onChange={(text) => handleChange(HERONAME, text)}
			/>
			<input
				type="text"
				value={alterEgo}
				placeholder={ALTEREGO}
				onChange={(text) => handleChange(ALTEREGO, text)}
			/>
			<input
				type="text"
				value={firstAppearance}
				placeholder={FIRSTAPPEARANCE}
				onChange={(text) => handleChange(FIRSTAPPEARANCE, text)}
			/>
			<p>
				<label>Publisher</label>
				<select id="publisher-list">
					<option value="Marvel Comics">Marvel</option>
					<option value="DC Comics">DC</option>
				</select>
			</p>
		</form>
	);
};

export default Search;
