function composeHeroQuery(heroesQueryObject) {
	let queryString = '';
	if (heroesQueryObject.firstAppearance !== '') {
		queryString = queryString.concat(
			`first_appearance=${heroesQueryObject.firstAppearance}&`
		);
	}
	if (heroesQueryObject.publisher !== '') {
		queryString = queryString.concat(
			`publisher=${heroesQueryObject.publisher}&`
		);
	}
	if (heroesQueryObject.alterEgo !== '') {
		queryString = queryString.concat(
			`alter_ego=${heroesQueryObject.alterEgo}&`
		);
	}
	if (heroesQueryObject.heroName !== '') {
		queryString = queryString.concat(
			`superhero=${heroesQueryObject.heroName}&`
		);
	}
	return queryString;
}

export default composeHeroQuery;
