import React, { useEffect, useState } from 'react';

import Search from './Search';
import HeroesList from './HeroesList';
import { url } from '../api/superheroesApi';
import formatQuery from '../helpers/formatQuery';

const App = () => {
	const [superheroes, setSuperHeroes] = useState(null);
	const [query, setQuery] = useState({
		alterEgo: '',
		firstAppearance: '',
		heroName: '',
		publisher: '',
	});
	const [loading, setLoading] = useState(true);

	useEffect(() => {
		(async () => {
			const { data } = await url.get(`?${formatQuery(query)}`);
			setSuperHeroes(data);
			setLoading(false);
		})();
		setLoading(true);
	}, [query]);

	if (loading) {
		return <div>Wrangling Heroes!</div>;
	} else {
		return (
			<div>
				<div>
					<Search onSubmit={(object) => setQuery(object)} />
				</div>
				<HeroesList heroState={superheroes} />
			</div>
		);
	}
};

export default App;
